<script>
 import Copy from '@/config/copy'

 export default {
   name: 'AppHeader',
   data() {
     return {
       copy: Copy
     }
   },
   methods: {
     scrollMeThere(ev) {
       const elemID = ev.target.href.split('/#')[1]
       let newElem = document.querySelector('#' + elemID)
       const opt = {top: newElem.offsetTop,
                    left: 0,
                    behavior: 'smooth'}
       window.scrollTo(opt)
       this.$router.push({hash: '#' + elemID})
     }
   }
 }
</script>
